package arrayShift;

import org.junit.Test;
import static org.junit.Assert.*;

public class ArrayShiftTest {
    @Test
    public void testRegularEvenNumberArray() {
       ArrayShift lOne = new ArrayShift();
        int[] inputArr =new int[] {2,4,6,8};
        int[] expectedArr =new int[] {2,4,5,6,8};
        assertArrayEquals(expectedArr, lOne.ArrayShift(inputArr,5));
    }


    @Test
    public void testRegularOddNumberArray(){
        ArrayShift lOne = new ArrayShift();
        int[] inputArr =new int[] {2,4,5,6,8};
        int[] expectedArr =new int[] {2,4,5,35,6,8};
        assertArrayEquals(expectedArr, lOne.ArrayShift(inputArr,35));
    }

    @Test
    public void testOneNumberArray(){
        ArrayShift lOne = new ArrayShift();
        int[] inputArr =new int[] {2};
        int[] expectedArr =new int[] {2,4};
        assertArrayEquals(expectedArr, lOne.ArrayShift(inputArr,4));
    }


    @Test
    public void testNullArray(){
        ArrayShift lOne = new ArrayShift();
        int[] inputArr =new int[] {};
        int[] expectedArr =new int[] {2};
        assertArrayEquals(expectedArr, lOne.ArrayShift(inputArr,2));
    }

}
