package binarySearch;

import org.junit.Test;
import static org.junit.Assert.*;

public class ArrayShiftTest {
    @Test public void testExsistIndex() {
        BinarySearch classUnderTest = new BinarySearch();
        int[] nums = { 4, 8, 15, 16, 23, 42 };
        int key = 15;
        assertEquals("should give you the index of the key in the array back",2,classUnderTest.binarySearch(nums,key));
    }


    @Test public void testNotExistsIndex() {
        BinarySearch classUnderTest = new BinarySearch();
        int[] nums = { 4, 8, 15, 16, 23, 42 };
        int key = 99;
        assertEquals("should give you  back -1 because it not exsits",-1,classUnderTest.binarySearch(nums,key));
    }

    @Test public void testOneNumArray() {
        BinarySearch classUnderTest = new BinarySearch();
        int[] nums = {4};
        int key = 4;
        assertEquals("should give you the index of the key in the array back",0,classUnderTest.binarySearch(nums,key));
    }


    @Test public void testTwoSameNumArray() {
        BinarySearch classUnderTest = new BinarySearch();
        int[] nums = {4,4};
        int key = 4;
        assertEquals("should give you the first index of the key in the array back",0,classUnderTest.binarySearch(nums,key));
    }
}
